-- Database (DDL)
CREATE DATABASE IF NOT EXISTS TweetCount;
USE TweetCount;

-- Tabel (DDL)
CREATE TABLE Category (
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(15) NOT NULL
);

CREATE TABLE Tweet (
    id INT AUTO_INCREMENT PRIMARY KEY,
    likes INT,
    content VARCHAR(150) NOT NULL,
    username VARCHAR(50) NOT NULL,
    category_id INT NOT NULL,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (category_id) REFERENCES Category(id)
);

-- User (DCL)
USE mysql;

CREATE USER IF NOT EXISTS 'public'@'localhost'
IDENTIFIED BY '';

GRANT SELECT
ON TweetCount.*
TO 'public'@'localhost';

USE TweetCount;

-- Trigger
DELIMITER $$

CREATE TRIGGER OnDeleteCategory
BEFORE DELETE ON Category
FOR EACH ROW
BEGIN
    DELETE FROM Tweet
    WHERE category_id = OLD.id;
END$$

DELIMITER ;

-- Insert (DML)




-- Select (Join & Subquery)
SELECT
  c.name AS kategori,
  COUNT(t.id) AS jumlah_tweet,
  (COUNT(t.id)/(SELECT COUNT(id) FROM Tweet) * 100) AS persentase
FROM Tweet AS t
JOIN Category AS c
ON t.category_id = c.id
GROUP BY category_id
ORDER BY jumlah_tweet;
